mapper.select:
/* file : BannerMapper.xml, id : getBannerList */ SELECT * FROM ST_BANNER WHERE DEL_FLAG = 0
AND TITLE LIKE CONCAT('%', #{keyword}, '%')
ORDER BY BANNER_SEQ DESC /*||ORDER BY BANNER_SEQ DESC||*/

mapper.insert:
/* file : BannerMapper.xml, id : insertBanner */
INSERT
INTO ST_BANNER (
TITLE
,URL
,URL_OPEN_TYPE
,USED
, REG_USER_SEQ
, REG_DATE)
VALUES (
#{title}
,#{url}
,#{urlOpenType}
,#{used}
, #{loginUserSeq}
, CURRENT_TIMESTAMP())


    SELECT LAST_INSERT_ID()

mapper.update:
/* file : BoardMapper.xml, id : updateBoard */
UPDATE ST_BANNER
SET TITLE   = #{title}
  , URL      = #{url}
  , URL_OPEN_TYPE  = #{urlOpenType}
  , USED     = #{used}
  , MOD_USER_SEQ = #{loginUserSeq}
  , MOD_DATE     = CURRENT_TIMESTAMP()
WHERE BANNER_SEQ    = #{bannerSeq}

mapper.select:
/* file : BannerMapper.xml, id : getBanner */
SELECT *
FROM ST_BANNER
WHERE DEL_FLAG  = 0
  AND BANNER_SEQ = #{bannerSeq}

mapper.update:
/* file : BannerMapper.xml, id : deleteBanner */
UPDATE ST_BANNER
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{login_user_seq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE BANNER_SEQ      = #{banner_seq}
