/* file : BoardMapper.xml, id : deleteAllBoard */
UPDATE ST_BOARD
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{login_user_seq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE BOARD_SETTING_SEQ    = #{board_setting_seq}

/* file : BoardMapper.xml, id : modPopupFlagToZero */
  UPDATE ST_BOARD
     SET POPUP_FLAG     = 0
   WHERE BOARD_SEQ   != #{boardSeq}
   and   POPUP_FLAG     = 1

mapper.select:
		/* file : BoardMapper.xml, id : getBoard */
		  SELECT A.*, U.NICK_NAME, DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS REG_DT
		    FROM ST_BOARD A
            LEFT JOIN ST_USER AS U ON U.USER_SEQ = A.REG_USER_SEQ
		   WHERE A.DEL_FLAG  = 0
		     AND A.BOARD_SEQ = #{boardSeq}

/* file : BoardMapper.xml, id : getBoardSetting */
SELECT A.*
FROM ST_BOARD_SETTING A
WHERE A.DEL_FLAG  = 0
  AND A.BOARD_SETTING_SEQ = #{boardSettingSeq}

/* file : BoardMapper.xml, id : getBoardLawList */ SELECT L.* , DATE_FORMAT(L.REG_DATE, '%Y-%m-%d') AS REG_DT , LAW_TYPE.CODE_NAME AS LAW_TYPE_NAME , LAW_CLASS.CODE_NAME AS LAW_CLASS_NAME , U.USERNAME AS REG_USERNAME , DATE_FORMAT(L.LAW_DATE, '%Y.%c.%e') AS LAW_DT , DATE_FORMAT(L.ENFORCE_DATE, '%Y.%c.%e') AS ENFORCE_DT , (SELECT COUNT(1) FROM ST_FILES F WHERE F.DEL_FLAG = 0 AND F.REF_TYPE = 'LAW' AND F.REF_SEQ = L.LAW_SEQ ) AS FILE_CNT FROM ST_LAW L INNER JOIN ST_USER U ON L.REG_USER_SEQ = U.USER_SEQ INNER JOIN ST_CODE_DTL LAW_TYPE ON LAW_TYPE.GRP_CODE = 'LAW_TYPE' AND LAW_TYPE.CODE = L.LAW_TYPE INNER JOIN ST_CODE_DTL LAW_CLASS ON LAW_CLASS.GRP_CODE = 'LAW_CLASS' AND LAW_CLASS.CODE = L.LAW_CLASS WHERE L.DEL_FLAG = 0
AND L.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
ORDER BY LAW_SEQ DESC /*||ORDER BY LAW_SEQ DESC||*/

/* file : BoardMapper.xml, id : getBoardProjectList */ SELECT P.* , DATE_FORMAT(P.REG_DATE, '%Y-%m-%d') AS REG_DT , PROJECT_TYPE.CODE_NAME AS PROJECT_TYPE_NAME , SIDO.CODE_NAME AS PLACE_NAME , U.USERNAME AS REG_USERNAME , (SELECT COUNT(1) FROM ST_FILES F WHERE F.DEL_FLAG = 0 AND F.REF_TYPE = 'PROJECT' AND F.REF_SEQ = P.PROJECT_SEQ ) AS FILE_CNT FROM ST_PROJECT P INNER JOIN ST_USER U ON P.REG_USER_SEQ = U.USER_SEQ INNER JOIN ST_CODE_DTL PROJECT_TYPE ON PROJECT_TYPE.GRP_CODE = 'PROJECT_TYPE' AND PROJECT_TYPE.CODE = P.PROJECT_TYPE INNER JOIN ST_CODE_DTL SIDO ON SIDO.GRP_CODE = 'SIDO' AND SIDO.CODE = P.PLACE WHERE P.DEL_FLAG = 0
AND P.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
ORDER BY PROJECT_SEQ DESC /*||ORDER BY PROJECT_SEQ DESC||*/

mapper.insert:
