mapper.insert:
/* file : CategoryMapper.xml, id : insertCategory2 */
INSERT
INTO ST_CATEGORY2 (
NAME
, CATEGORY1_SEQ
, REG_USER_SEQ
, REG_DATE)
VALUES (
#{name}
, #{category1Seq}
, #{loginUserSeq}
, CURRENT_TIMESTAMP())


    SELECT LAST_INSERT_ID()

