mapper.select:
/* file : CategoryMapper.xml, id : getCategory2DelInfo */
SELECT group_concat(CATEGORY2_SEQ) as DEL_GROUP_SEQ
FROM ST_CATEGORY2
WHERE DEL_FLAG = 0 AND CATEGORY1_SEQ = #{categorySeq}
