mapper.select:
/* file : ConsultingMapper.xml, id : getConsultingList */ SELECT C.* , CATE1.NAME AS CATEGORY_1_NAME , CATE2.NAME AS CATEGORY_2_NAME , CATE3.NAME AS CATEGORY_3_NAME , SUPPORT.SUPPORT_NAME AS SUPPORT_ORG_NAME , DATE_FORMAT(C.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME FROM ST_CONSULTING C INNER JOIN ST_USER U ON C.REG_USER_SEQ = U.USER_SEQ inner join ST_CATEGORY1 as CATE1 ON CATE1.CATEGORY1_SEQ = C.CATEGORY_1 inner join ST_CATEGORY2 as CATE2 ON CATE2.CATEGORY2_SEQ = C.CATEGORY_2 inner join ST_CATEGORY3 as CATE3 ON CATE3.CATEGORY3_SEQ = C.CATEGORY_3 left join ST_SUPPORT as SUPPORT ON SUPPORT.SUPPORT_SEQ = C.SUPPORT_ORG WHERE C.DEL_FLAG = 0
AND C.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
AND C.CATEGORY_1 = #{category1}
AND C.CATEGORY_2 = #{category2}
AND C.CATEGORY_3 = #{category3}
ORDER BY CON_SEQ DESC /*||ORDER BY CON_SEQ DESC||*/

mapper.insert:
/* file : ConsultingMapper.xml, id : insertConsulting */
INSERT
  INTO ST_CONSULTING (
            CATEGORY_1
            , CATEGORY_2
            , CATEGORY_3
            , SUBJECT
            , CONTENT
            , SUPPORT_CONTENT
            , SUPPORT_BY
            , REGION
            , SUPPORT_ORG
            , CON_URL
            , RECOMMEND_FLAG
            , TAGS
            , REG_USER_SEQ)
       VALUES (
			#{category1}
			, #{category2}
			, #{category3}
            , #{subject}
            , #{content}
			, #{supportContent}
			, #{supportBy}
			, #{region}
            , #{supportOrg}
            , #{conUrl}
            , #{recommendFlag}
            , #{tags}
            , #{loginUserSeq})


	SELECT LAST_INSERT_ID()

mapper.update:
/* file : ConsultingMapper.xml, id : updateConsulting */
UPDATE ST_CONSULTING
   SET CATEGORY_1     = #{category1}
     , CATEGORY_2     = #{category2}
     , CATEGORY_3     = #{category3}
     , SUBJECT        = #{subject}
	 , CONTENT        = #{content}
	 , SUPPORT_CONTENT= #{supportContent}
	 , SUPPORT_BY     = #{supportBy}
	 , REGION         = #{region}
     , SUPPORT_ORG    = #{supportOrg}
     , CON_URL        = #{conUrl}
     , RECOMMEND_FLAG = #{recommendFlag}
     , TAGS           = #{tags}
     , MOD_USER_SEQ   = #{loginUserSeq}
     , MOD_DATE       = CURRENT_TIMESTAMP()
 WHERE CON_SEQ        = #{conSeq}

/* file : ConsultingMapper.xml, id : deleteConsulting */
  UPDATE ST_CONSULTING
     SET DEL_FLAG     = 1
       , DEL_USER_SEQ = #{login_user_seq}
       , DEL_DATE     = CURRENT_TIMESTAMP()
   WHERE CON_SEQ      = #{con_seq}

mapper.select:
/* file : ConsultingMapper.xml, id : getConsulting */
  SELECT A.*
    FROM ST_CONSULTING A
   WHERE A.DEL_FLAG = 0
     AND A.CON_SEQ  = #{conSeq}
