mapper.select:
/* file : EducationMapper.xml, id : getEducationList */ SELECT E.* , CATE1.NAME AS CATEGORY_1_NAME , CATE2.NAME AS CATEGORY_2_NAME , CATE3.NAME AS CATEGORY_3_NAME , SUPPORT.SUPPORT_NAME AS SUPPORT_ORG_NAME , DATE_FORMAT(E.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME , F.THUMBNAIL_PATH , F.FILE_NAME FROM ST_EDUCATION E INNER JOIN ST_USER U ON E.REG_USER_SEQ = U.USER_SEQ inner join ST_CATEGORY1 as CATE1 ON CATE1.CATEGORY1_SEQ = E.CATEGORY_1 inner join ST_CATEGORY2 as CATE2 ON CATE2.CATEGORY2_SEQ = E.CATEGORY_2 inner join ST_CATEGORY3 as CATE3 ON CATE3.CATEGORY3_SEQ = E.CATEGORY_3 left join ST_SUPPORT as SUPPORT ON SUPPORT.SUPPORT_SEQ = E.SUPPORT_ORG LEFT JOIN ST_FILES F ON F.REF_TYPE = 'EDUCATION_IMAGE' AND F.REF_SEQ = E.EDU_SEQ AND F.DEL_FLAG = 0 WHERE E.DEL_FLAG = 0
AND E.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
AND E.CATEGORY_1 = #{category1}
AND E.CATEGORY_2 = #{category2}
AND E.CATEGORY_3 = #{category3}
AND E.SUPPORT_ORG = #{support_org}
ORDER BY EDU_SEQ DESC /*||ORDER BY EDU_SEQ DESC||*/

mapper.insert:
/* file : EducationMapper.xml, id : insertEducation */
INSERT
  INTO ST_EDUCATION (
              CATEGORY_1
            , CATEGORY_2
            , CATEGORY_3
            , SUBJECT
            , CONTENT
            , SUPPORT_ORG
            , EDU_URL
            , BIZ_TYPE
            , RECOMMEND_FLAG
            , TAGS
            , REG_USER_SEQ)
       VALUES (
              #{category1}
            , #{category2}
            , #{category3}
            , #{subject}
            , #{content}
            , #{supportOrg}
            , #{eduUrl}
            , #{bizType}
            , #{recommendFlag}
            , #{tags}
            , #{loginUserSeq})


	SELECT LAST_INSERT_ID()

mapper.update:
/* file : EducationMapper.xml, id : updateEducation */
UPDATE ST_EDUCATION
   SET CATEGORY_1     = #{category1}
     , CATEGORY_2     = #{category2}
     , CATEGORY_3     = #{category3}
     , SUBJECT        = #{subject}
     , CONTENT        = #{content}
     , SUPPORT_ORG    = #{supportOrg}
     , EDU_URL        = #{eduUrl}
     , BIZ_TYPE       = #{bizType}
     , RECOMMEND_FLAG = #{recommendFlag}
     , TAGS           = #{tags}
     , MOD_USER_SEQ   = #{loginUserSeq}
     , MOD_DATE       = CURRENT_TIMESTAMP()
 WHERE EDU_SEQ        = #{eduSeq}

/* file : EducationMapper.xml, id : deleteEducation */
  UPDATE ST_EDUCATION
     SET DEL_FLAG     = 1
       , DEL_USER_SEQ = #{login_user_seq}
       , DEL_DATE     = CURRENT_TIMESTAMP()
   WHERE EDU_SEQ      = #{edu_seq}

mapper.select:
/* file : EducationMapper.xml, id : getEducation */
  SELECT A.*
    FROM ST_EDUCATION A
   WHERE A.DEL_FLAG = 0
     AND A.EDU_SEQ  = #{eduSeq}
