mapper.select:
/* file : FaqMapper.xml, id : getFaqList */ SELECT A.* , DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME FROM ST_FAQ A INNER JOIN ST_USER U ON A.REG_USER_SEQ = U.USER_SEQ WHERE A.DEL_FLAG = 0
AND A.TYPE = #{type}
AND A.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
ORDER BY A.FAQ_SEQ DESC /*||ORDER BY FAQ_SEQ DESC||*/

mapper.insert:
/* file : FaqMapper.xml, id : insertFaqBoard */
INSERT
INTO ST_FAQ (
TYPE
,TYPE_NAME
,TYPE_COLOR
, SUBJECT
, CONTENT
, REG_USER_SEQ
, REG_DATE)
VALUES

    (
    #{item.type}
    , #{item.typeName}
    , #{item.typeColor}
    , #{item.subject}
    , #{item.content}
    , #{item.loginUserSeq}
    , CURRENT_TIMESTAMP()
    )




    SELECT LAST_INSERT_ID()

mapper.update:
/* file : FaqMapper.xml, id : deleteAllFaq */
UPDATE ST_FAQ
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{loginUserSeq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE DEL_FLAG    = 0

mapper.select:
/* file : FaqMapper.xml, id : getFaq */
SELECT A.*, U.NICK_NAME, DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS REG_DT
FROM ST_FAQ A
         LEFT JOIN ST_USER AS U ON U.USER_SEQ = A.REG_USER_SEQ
WHERE A.DEL_FLAG  = 0
  AND A.FAQ_SEQ = #{faqSeq}

mapper.update:
/* file : FaqMapper.xml, id : updateFaq */
UPDATE ST_FAQ
SET TYPE   = #{type}
  , TYPE_COLOR = #{typeColor}
  , TYPE_NAME = #{typeName}
  , SUBJECT      = #{subject}
  , CONTENT      = #{content}
  , MOD_USER_SEQ = #{loginUserSeq}
  , MOD_DATE     = CURRENT_TIMESTAMP()
WHERE FAQ_SEQ    = #{faqSeq}

/* file : FaqMapper.xml, id : deleteFaq */
UPDATE ST_FAQ
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{login_user_seq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE FAQ_SEQ      = #{faq_seq}
