mapper.select:
/* file : FilesMapper.xml, id : getFileList */
SELECT *
  FROM ST_FILES
 WHERE DEL_FLAG = 0
   AND REF_TYPE = #{ref_type}
   AND REF_SEQ  = #{ref_seq}

/* file : FilesMapper.xml, id : getFile */
SELECT *
  FROM ST_FILES
 WHERE DEL_FLAG = 0
   AND FILE_SEQ = #{file_seq}

mapper.insert:
/* file : FilesMapper.xml, id : insertFile */
INSERT
  INTO ST_FILES (
              REF_TYPE
            , REF_SEQ
            , FILE_TYPE
            , FILE_NAME
            , FILE_TITLE
            , FILE_PATH
            , FILE_EXT
            , FILE_BYTE
            , FILE_SIZE
            , FILE_CONTENT_TYPE
            , THUMBNAIL_PATH
            , REG_USER_SEQ)
       VALUES (
              #{refType}
            , #{refSeq}
            , #{fileType}
            , #{fileName}
            , #{fileTitle}
            , #{filePath}
            , #{fileExt}
            , #{fileByte}
            , #{fileSize}
            , #{fileContentType}
            , #{thumbnailPath}
            , #{loginUserSeq} )


	SELECT LAST_INSERT_ID()

mapper.update:
/* file : FilesMapper.xml, id : updateFile */
UPDATE ST_FILES
   SET FILE_PATH         = #{filePath}
     , FILE_CONTENT_TYPE = #{fileContentType}
     , THUMBNAIL_PATH    = #{thumbnailPath}
 WHERE FILE_SEQ          = #{fileSeq}

/* file : FilesMapper.xml, id : deleteFile */
UPDATE ST_FILES
   SET DEL_FLAG     = 1
     , DEL_USER_SEQ = #{login_user_seq}
     , DEL_DATE     = CURRENT_TIMESTAMP()
 WHERE FILE_SEQ     = #{file_seq}

/* file : FilesMapper.xml, id : deleteFileAll */
UPDATE ST_FILES
   SET DEL_FLAG     = 1
     , DEL_USER_SEQ = #{login_user_seq}
     , DEL_DATE     = CURRENT_TIMESTAMP()
 WHERE REF_TYPE     = #{ref_type}

/* file : FilesMapper.xml, id : updateFileTitle */
UPDATE ST_FILES
   SET FILE_TITLE   = #{fileTitle}
     , MOD_USER_SEQ = #{loginUserSeq}
     , MOD_DATE     = CURRENT_TIMESTAMP()
 WHERE FILE_SEQ     = #{fileSeq}
