mapper.select:
/* file : MenuMapper.xml, id : getBreadcrumb */
SELECT A.MENU_CODE
     , A.MENU_NAME
     , (SELECT MENU_NAME
          FROM ST_MENU B
         WHERE B.MENU_CODE = A.PARENT_MENU_CODE
       ) AS PARENT_MENU_NAME
  FROM ST_MENU A
 WHERE A.MENU_CODE = #{menuCode}
   AND A.DEL_FLAG  = 0

/* file : MenuMapper.xml, id : getAuthMenuList */ WITH RECURSIVE CTE AS ( SELECT MENU_CODE , MENU_NAME , PARENT_MENU_CODE , 1 AS LEVEL FROM ST_MENU WHERE PARENT_MENU_CODE = 'TOP' AND DEL_FLAG = 0
UNION ALL
SELECT M.MENU_CODE , M.MENU_NAME , M.PARENT_MENU_CODE , 1 + CTE.LEVEL AS LEVEL FROM ST_MENU M INNER JOIN CTE ON M.PARENT_MENU_CODE = CTE.MENU_CODE WHERE M.DEL_FLAG = 0 ) SELECT A.MENU_CODE AS ID , A.MENU_NAME AS TEXT , A.PARENT_MENU_CODE AS PARENT_ID , LEVEL , IFNULL(B.READ_FLAG, 0) AS VALUE FROM CTE A LEFT OUTER JOIN ST_MENU_AUTH B ON A.MENU_CODE = B.MENU_CODE AND B.AUTH = #{auth} ORDER BY A.MENU_CODE

mapper.update:
/* file : MenuMapper.xml, id : deleteAuthMenu */
UPDATE ST_MENU_AUTH
   SET DEL_FLAG     = 1
     , DEL_USER_SEQ = #{login_user_seq}
     , DEL_DATE     = CURRENT_TIMESTAMP()
 WHERE AUTH         = #{auth}

mapper.insert:
/* file : MenuMapper.xml, id : saveMenu */
INSERT
  INTO ST_MENU (
              MENU_CODE
            , MENU_NAME
            , PARENT_MENU_CODE
            , MENU_LINK
            , MENU_ICON
            , ORDBY
            , REG_USER_SEQ)
       VALUES (
              UPPER( #{menu_code} )
            , #{menu_name}
            , #{parent_menu_code}
            , #{menu_link}
            , #{menu_icon}
            , #{ordby}
            , #{login_user_seq})
    ON DUPLICATE KEY UPDATE MENU_NAME = #{menu_name}
     , PARENT_MENU_CODE               = #{parent_menu_code}
     , MENU_LINK                      = #{menu_link}
     , MENU_ICON                      = #{menu_icon}
     , ORDBY                          = #{ordby}
     , DEL_FLAG                       = 0
     , MOD_USER_SEQ                   = #{login_user_seq}
     , MOD_DATE                       = CURRENT_TIMESTAMP()

mapper.update:
/* file : MenuMapper.xml, id : deleteMenu */
  UPDATE ST_MENU
     SET DEL_FLAG     = 1
       , DEL_USER_SEQ = #{login_user_seq}
       , DEL_DATE     = CURRENT_TIMESTAMP()
   WHERE MENU_CODE    = #{menu_code}

/* file : MenuMapper.xml, id : deleteMenuParent */
  UPDATE ST_MENU
     SET DEL_FLAG         = 1
       , DEL_USER_SEQ     = #{login_user_seq}
       , DEL_DATE         = CURRENT_TIMESTAMP()
   WHERE PARENT_MENU_CODE = #{menu_code}

mapper.select:
/* file : MenuMapper.xml, id : getMenu */
SELECT A.*
  FROM ST_MENU A
 WHERE A.MENU_CODE = #{menuCode}
   AND A.DEL_FLAG  = 0
