mapper.select:
/* file : QuestionSettingMapper.xml, id : getQuestionSettingList */
SELECT *, DATE_FORMAT(REG_DATE, '%Y-%m-%d') AS REG_DT
FROM ST_QUESTION_SETTING
WHERE DEL_FLAG            = 0

    AND TITLE LIKE CONCAT('%', #{keyword}, '%')


    AND QUESTION_TYPE        = #{field}


ORDER BY QUESTION_SETTING_SEQ DESC
/*||ORDER BY QUESTION_SETTING_SEQ DESC||*/

/* file : BoardMapper.xml, id : getBoardList */ SELECT A.* , DATE_FORMAT(A.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME
, C.CODE_NAME AS CATEGORY_NAME , C.REF_CLASS AS CATEGORY_CLASS
, (SELECT COUNT(1) FROM ST_FILES F WHERE F.DEL_FLAG = 0 /*AND F.REF_TYPE = 'BOARD'*/ AND F.REF_SEQ = A.BOARD_SEQ ) AS FILE_CNT
, AW.COMMENT FROM ST_BOARD A INNER JOIN ST_USER U ON A.REG_USER_SEQ = U.USER_SEQ LEFT JOIN ST_BOARD_SETTING AS BS ON BS.BOARD_SETTING_SEQ = A.BOARD_SETTING_SEQ LEFT JOIN ST_ANSWER AS AW ON AW.BOARD_SEQ = A.BOARD_SEQ
INNER JOIN ST_CODE_DTL C ON C.GRP_CODE = 'FAQ_TYPE' AND C.CODE = A.CATEGORY
WHERE A.DEL_FLAG = 0 AND A.BOARD_SETTING_SEQ = #{boardSettingSeq}
AND A.CATEGORY = #{category}
AND A.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
ORDER BY A.BOARD_SEQ DESC /*||ORDER BY BOARD_SEQ DESC||*/

mapper.insert:
/* file : BoardMapper.xml, id : insertBoard */
INSERT
INTO ST_BOARD (
BOARD_TYPE
, BOARD_SETTING_SEQ
, SUBJECT
, CONTENT
, CATEGORY
, NOTICE_FLAG
, POPUP_FLAG
, FROM_DT
, TO_DT
, TAGS
, REG_USER_SEQ
, REG_DATE)
VALUES (
#{boardType}
, #{boardSettingSeq}
, #{subject}
, #{content}
, #{category}
, #{noticeFlag}
, #{popupFlag}
, #{fromDt}
, #{toDt}
, #{tags}
, #{loginUserSeq}
, CURRENT_TIMESTAMP())


    SELECT LAST_INSERT_ID()

mapper.update:
/* file : BoardMapper.xml, id : updateBoard */
UPDATE ST_BOARD
SET BOARD_TYPE   = #{boardType}
  , SUBJECT      = #{subject}
  , CONTENT      = #{content}
  , CATEGORY     = #{category}
  , NOTICE_FLAG  = #{noticeFlag}
  , POPUP_FLAG   = #{popupFlag}
  , FROM_DT      = #{fromDt}
  , TO_DT        = #{toDt}
  , TAGS         = #{tags}
  , MOD_USER_SEQ = #{loginUserSeq}
  , MOD_DATE     = CURRENT_TIMESTAMP()
WHERE BOARD_SEQ    = #{boardSeq}

/* file : BoardMapper.xml, id : deleteBoard */
UPDATE ST_BOARD
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{login_user_seq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE BOARD_SEQ    = #{board_seq}

mapper.insert:
/* file : QuestionSettingMapper.xml, id : insertQuestionSetting */
INSERT
INTO ST_QUESTION_SETTING (
QUESTION_TYPE
, TITLE
, DESCRIPTION
, MULTIPLE_USE
, RANK_CHANGE_USE
, MAXIMUM_USE
, MAXIMUM_NUM
, KEYWORD
, ANSWER_TYPE
, REG_USER_SEQ
, REG_DATE)
VALUES (
#{questionType}
, #{title}
, #{description}
, #{multipleUse}
, #{rankChangeUse}
, #{maximumUse}
, #{maximumNum}
, #{keyword}
, #{answerType}
, #{loginUserSeq}
, CURRENT_TIMESTAMP())


    SELECT LAST_INSERT_ID()

mapper.update:
/* file : QuestionMapper.xml, id : updateQuestionSetting */
UPDATE ST_QUESTION_SETTING
SET
  TITLE = #{title}
  , DESCRIPTION = #{description}
  , MULTIPLE_USE = #{multipleUse}
  , RANK_CHANGE_USE = #{rankChangeUse}
  , MAXIMUM_USE = #{maximumUse}
  , MAXIMUM_NUM = #{maximumNum}
  , KEYWORD = #{keyword}
  , ANSWER_TYPE = #{answerType}
  , MOD_USER_SEQ = #{loginUserSeq}
  , MOD_DATE     = CURRENT_TIMESTAMP()
WHERE QUESTION_SETTING_SEQ    = #{questionSettingSeq}

mapper.select:
/* file : QuestionSettingMapper.xml, id : getQuestionSetting */
SELECT A.*
FROM ST_QUESTION_SETTING A
WHERE A.DEL_FLAG  = 0
  AND A.QUESTION_SETTING_SEQ = #{questionSettingSeq}
