mapper.select:
/* file : ReSearchShopMapper.xml, id : getReSearchShopList */
SELECT *
FROM ST_RESEARCH_SHOP
where 1=1 and DEL_FLAG = 0


    AND SHOP_NM LIKE CONCAT('%', #{keyword}, '%')


    AND CODE_TYPE1              = #{category1}


    AND CODE_TYPE2              = #{category2}


    AND CODE_TYPE3              = #{category3}

ORDER BY SHOP_SEQ DESC
/*||ORDER BY SHOP_SEQ DESC||*/

/* file : ReSearchShopMapper.xml, id : getReSearchShopAll */
select * from ST_RESEARCH_SHOP
where 1=1

    AND SHOP_SEQ IN

        #{item}


order by SHOP_SEQ desc ;

mapper.update:
/* file : ReSearchShopMapper.xml, id : deleteEducation */
UPDATE ST_RESEARCH_SHOP
SET DEL_FLAG     = 1
  , DEL_USER_SEQ = #{login_user_seq}
  , DEL_DATE     = CURRENT_TIMESTAMP()
WHERE SHOP_SEQ      = #{seq}

mapper.insert:
/* file : ReSearchShopMapper.xml, id : insertReSearchShopExcel */
INSERT
INTO ST_RESEARCH_SHOP (
SHOP_NO
, SHOP_NM
, BRANCH
, CODE_TYPE1
, NM_TYPE1
, CODE_TYPE2
, NM_TYPE2
, CODE_TYPE3
, NM_TYPE3
, ADDR_CD
, ADDR
, ST_ADDR
, EMD_CD
, EMD_NM
, LONGITUDE
, LATITUDE
, SUB_STA_NM
, SUB_STA_NO
, AVE_SUB_PASS_ON
, AVE_SUB_PASS_OFF
, SUM_SUB_PASS_ON
, SUM_SUB_PASS_OFF
, BUS_STA_NM
, ARS_ID
, AVE_BUS_PASS_ON
, AVE_BUS_PASS_OFF
, SUM_BUS_PASS_ON
, SUM_BUS_PASS_OFF
, APPRV_DATE
, CT_GRD
, CT_BASE
, PINCP_USE_CD
, PRINCP_USE
, OTHER_USE
, REG_USER_SEQ
, REG_DATE)
VALUES

    (
    #{item.shopNo}
    , #{item.shopNm}
    , #{item.branch}
    , #{item.codeType1}
    , #{item.nmType1}
    , #{item.codeType2}
    , #{item.nmType2}
    , #{item.codeType3}
    , #{item.nmType3}
    , #{item.addrCd}
    , #{item.addr}
    , #{item.stAddr}
    , #{item.emdCd}
    , #{item.emdNm}
    , #{item.longitude}
    , #{item.latitude}
    , #{item.subStaNm}
    , #{item.subStaNo}
    , #{item.aveSubPassOn}
    , #{item.aveSubPassOff}
    , #{item.sumSubPassOn}
    , #{item.sumSubPassOff}
    , #{item.busStaNm}
    , #{item.arsId}
    , #{item.aveBusPassOn}
    , #{item.aveBusPassOff}
    , #{item.sumBusPassOn}
    , #{item.sumBusPassOff}
    , #{item.apprvDate}
    , #{item.ctGrd}
    , #{item.ctBase}
    , #{item.pincpUseCd}
    , #{item.pincpUse}
    , #{item.otherUse}
    , #{item.loginUserSeq}
    , CURRENT_TIMESTAMP()
    )




    SELECT LAST_INSERT_ID()

mapper.select:
/* file : ReSearchShopMapper.xml, id : checkReSearchShop */
SELECT count(*)
FROM ST_RESEARCH_SHOP
