mapper.update:
/* file : UserMapper.xml, id : updateLastLoginDate */
UPDATE ST_USER
   SET LAST_LOGIN_DATE = CURRENT_TIMESTAMP
     , LOCK_CNT        = 0
 WHERE USER_SEQ        = #{userSeq}

mapper.select:
/* file : UserMapper.xml, id : getUserList */
  SELECT U.*
   		, DATE_FORMAT(U.WITHDRAW_DATE, '%Y-%m-%d')  AS WITHDRAW_DT
    FROM ST_USER U
   WHERE U.DEL_FLAG = 0
     AND U.USER_SEQ = #{userSeq}

mapper.insert:
/* file : UserMapper.xml, id : saveUser */
INSERT
  INTO ST_USER (
              USER_SEQ
            , ID
            , PASSWORD
            , USERNAME
            , EMAIL
            , TEL
            , HP
            , COMPANY
            , COMPANY_NAME
            , DEPT
            , POSITION
            , USER_TYPE
            , BIRTH_DAY
            , DESCRIPTION
            , AUTH
            , REG_USER_SEQ)
       VALUES (
              #{userSeq}
            , LOWER(#{id})
            , #{password}
            , #{username}
            , #{email}
            , #{tel}
            , #{hp}
            , #{company}
            , #{companyName}
            , #{dept}
            , #{position}
            , #{userType}
            , #{birthDay}
            , #{description}
            , #{auth}
            , #{loginUserSeq})
    ON DUPLICATE KEY UPDATE USERNAME = #{username}
     , EMAIL                         = #{email}
     , TEL                           = #{tel}
     , HP                            = #{hp}
     , COMPANY                       = #{company}
     , COMPANY_NAME                  = #{companyName}
     , DEPT                          = #{dept}
     , POSITION                      = #{position}
     , USER_TYPE                     = #{userType}
     , BIRTH_DAY                     = #{birthDay}
     , DESCRIPTION                   = #{description}
     , AUTH                          = #{auth}
     , DEL_FLAG                      = 0
     , MOD_USER_SEQ                  = #{loginUserSeq}
     , MOD_DATE                      = CURRENT_TIMESTAMP()

mapper.update:
/* file : UserMapper.xml, id : deleteUser */
  UPDATE ST_USER
     SET DEL_FLAG         = 1
       , DEL_USER_SEQ     = #{login_user_seq}
       , DEL_DATE         = CURRENT_TIMESTAMP()
   WHERE USER_SEQ         = #{user_seq}
