mapper.select:
/* file : AreaMapper.xml, id : getTradingAreaMapPartList */
SELECT TAM.AREA_SEQ, TAM.AREA_NAME, TA.AREA_TYPE, TA.AREA_CD
FROM ST_TRADING_AREA_MAP TAM
		 INNER JOIN ST_TRADING_AREA TA
					ON TAM.AREA_SEQ = TA.AREA_SEQ
		 INNER JOIN ST_TRADING_AREA_TYPE TAT
					ON TA.AREA_TYPE = TAT.AREA_TYPE
WHERE TAT.AREA_TYPE = #{code_type2}
  AND TAM.LATITUDE BETWEEN #{x1} AND #{x2}
  AND TAM.LONGITUDE BETWEEN #{y1} AND #{y2}
GROUP BY TAM.AREA_SEQ

/* file : AreaMapper.xml, id : getTradingAreaMapList */ SELECT B.MAP_SEQ , B.AREA_SEQ , B.MAP_TYPE , B.LATITUDE , B.LONGITUDE FROM ST_TRADING_AREA A INNER JOIN ST_TRADING_AREA_MAP B ON A.AREA_SEQ = B.AREA_SEQ WHERE A.DEL_FLAG = 0 AND B.DEL_FLAG = 0
AND B.MAP_TYPE = #{mapType}
ORDER BY B.MAP_TYPE DESC , B.MAP_SEQ

/* file : AreaMapper.xml, id : getRegionAreaMapList */ SELECT B.MAP_SEQ , B.AREA_SEQ , B.MAP_TYPE , B.LATITUDE , B.LONGITUDE FROM ST_REGION_AREA A INNER JOIN ST_REGION_AREA_MAP B ON A.AREA_SEQ = B.AREA_SEQ WHERE A.DEL_FLAG = 0 AND B.DEL_FLAG = 0
AND B.MAP_TYPE = #{mapType}
ORDER BY B.MAP_TYPE DESC , B.MAP_SEQ

/* file : AreaMapper.xml, id : getResearchShopList */
SELECT *
FROM ST_RESEARCH_SHOP RS
WHERE RS.CODE_TYPE1 IN (${scope})
  AND RS.LATITUDE BETWEEN #{x1} AND #{x2}
  AND RS.LONGITUDE BETWEEN #{y1} AND #{y2}

/* file : AreaMapper.xml, id : getTradingAreaShopList */
SELECT RS.CODE_TYPE1, RS.LONGITUDE, RS.LATITUDE
FROM ST_RESEARCH_SHOP RS
WHERE RS.CODE_TYPE1 IN (${scope})
  AND RS.LATITUDE BETWEEN #{x1} AND #{x2}
  AND RS.LONGITUDE BETWEEN #{y1} AND #{y2}

/* file : AreaMapper.xml, id : getTradingAreaDetails */
SELECT *
FROM ST_RESEARCH_AREA RA
WHERE RA.AREA_CD = #{areaCd}
  AND  RA.YEAR =  (SELECT YEAR FROM ST_RESEARCH_AREA ORDER BY YEAR DESC, QRT DESC LIMIT 1 )
	AND RA.QRT =  (SELECT QRT FROM ST_RESEARCH_AREA ORDER BY YEAR DESC, QRT DESC LIMIT 1 )

/* file : AreaMapper.xml, id : getTradingAreaDetails */
SELECT RA.AREA_SEQ AS AREA_SEQ, RA.YEAR AS YEAR, RA.QRT AS QRT, RA.IDX_STB_AREA AS IDX_STB_AREA
FROM ST_RESEARCH_AREA RA
WHERE RA.AREA_CD = #{areaCd}

/* file : AreaMapper.xml, id : getRegionAreainfoList */
SELECT RAC.EMD_CD AS EMD_CD, SUM(RAC.CT_SHOP) AS STORES, SUM(RAC.CT_FRANCHISE) AS FRANC FROM ST_RESEARCH_AREA_COM RAC WHERE RAC.YEAR = (SELECT YEAR FROM ST_RESEARCH_AREA_COM ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) AND RAC.QRT = (SELECT QRT FROM ST_RESEARCH_AREA_COM ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) GROUP BY RAC.EMD_CD
SELECT * FROM ST_RESEARCH_REGION RA WHERE RA.YEAR = (SELECT YEAR FROM ST_RESEARCH_REGION ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) AND RA.QRT = (SELECT QRT FROM ST_RESEARCH_REGION ORDER BY YEAR DESC, QRT DESC LIMIT 1 )

/* file : AreaMapper.xml, id : getRegionAreaDetail */
SELECT * FROM ST_RESEARCH_REGION RA WHERE RA.EMD_CD = #{emd_cd} AND RA.YEAR = (SELECT YEAR FROM ST_RESEARCH_REGION ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) AND RA.QRT = (SELECT QRT FROM ST_RESEARCH_REGION ORDER BY YEAR DESC, QRT DESC LIMIT 1 )
SELECT * FROM ST_RESEARCH_REGION RA WHERE RA.EMD_CD = #{emd_cd}
SELECT * , LEFT(RAC.COM_CD2, 1) AS DIVISION1 FROM ST_RESEARCH_AREA_COM RAC WHERE RAC.EMD_CD = #{emd_cd} AND RAC.YEAR = (SELECT YEAR FROM ST_RESEARCH_AREA_COM ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) AND RAC.QRT = (SELECT QRT FROM ST_RESEARCH_AREA_COM ORDER BY YEAR DESC, QRT DESC LIMIT 1 ) AND RAC.COM_CD2 != ''
