mapper.select:
/* file : CounselingMapper.xml, id : getCounselingList */ SELECT C.* , SUPPORT.SUPPORT_NAME AS SUPPORT_ORG_NAME , DATE_FORMAT(C.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME , CASE WHEN C.FIELD = 1 THEN '법률' WHEN C.FIELD = 2 THEN '노무' WHEN C.FIELD = 3 THEN '세무' WHEN C.FIELD = 4 THEN '회계' WHEN C.FIELD = 5 THEN '지식재산' WHEN C.FIELD = 6 THEN '관세' WHEN C.FIELD = 7 THEN '법무' WHEN C.FIELD = 8 THEN '경영 컨설팅' END as C_FIELD FROM ST_COUNSELING C INNER JOIN ST_USER U ON C.REG_USER_SEQ = U.USER_SEQ left join ST_SUPPORT as SUPPORT ON SUPPORT.SUPPORT_SEQ = C.SUPPORT_ORG WHERE C.DEL_FLAG = 0
AND C.TAGS LIKE CONCAT('%', #{keyword}, '%')
AND C.FIELD IN
#{item}
AND C.SUPPORT_ORG IN
#{item}
AND C.YEAR IN
#{item}
ORDER BY COU_SEQ DESC /*||ORDER BY COU_SEQ DESC||*/

mapper.update:
/* file : CounselingMapper.xml, id : updateCounselingReadCnt */
UPDATE ST_COUNSELING
   SET READ_CNT = IFNULL(READ_CNT, 0) + 1
 WHERE COU_SEQ  = #{couSeq}
