mapper.select:
/* file : EducationMapper.xml, id : getEducationList */ SELECT E.* , CATE1.NAME AS CATEGORY_1_NAME , CATE2.NAME AS CATEGORY_2_NAME , CATE3.NAME AS CATEGORY_3_NAME , SUPPORT.SUPPORT_NAME AS SUPPORT_ORG_NAME , DATE_FORMAT(E.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME , F.FILE_SEQ , F.FILE_PATH , F.THUMBNAIL_PATH , F.CRAWL_URL , EB.EDU_MARK_SEQ , EW.EDU_WATCHING_SEQ FROM ST_EDUCATION E INNER JOIN ST_USER U ON E.REG_USER_SEQ = U.USER_SEQ inner join ST_CATEGORY1 as CATE1 ON CATE1.CATEGORY1_SEQ = E.CATEGORY_1 inner join ST_CATEGORY2 as CATE2 ON CATE2.CATEGORY2_SEQ = E.CATEGORY_2 inner join ST_CATEGORY3 as CATE3 ON CATE3.CATEGORY3_SEQ = E.CATEGORY_3 left join ST_SUPPORT as SUPPORT ON SUPPORT.SUPPORT_SEQ = E.SUPPORT_ORG LEFT JOIN ST_FILES F ON F.REF_TYPE = 'EDUCATION_IMAGE' AND F.REF_SEQ = E.EDU_SEQ AND F.DEL_FLAG = 0 left join ST_EDUCATION_BOOKMARK as EB ON EB.EDU_SEQ = E.EDU_SEQ and EB.USER_SEQ = #{login_user_seq} left join ST_EDUCATION_WATCHING as EW ON EW.EDU_SEQ = E.EDU_SEQ and EW.USER_SEQ = #{login_user_seq} WHERE E.DEL_FLAG = 0 AND E.EDU_URL != ''
AND E.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
AND E.RECOMMEND_FLAG = 1
AND E.CATEGORY_1 = #{category1}
AND E.CATEGORY_2 = #{category2}
AND E.CATEGORY_3 = #{category3}
AND E.SUPPORT_ORG = #{support_org}
ORDER BY READ_CNT DESC, EDU_SEQ DESC /*||ORDER BY READ_CNT DESC, EDU_SEQ DESC||*/
ORDER BY RAND()
ORDER BY EDU_SEQ DESC /*||ORDER BY EDU_SEQ DESC||*/

/* file : EducationMapper.xml, id : getEducationRecommendList */ SELECT E.* , CATE1.NAME AS CATEGORY_1_NAME , CATE2.NAME AS CATEGORY_2_NAME , CATE3.NAME AS CATEGORY_3_NAME , SUPPORT.SUPPORT_NAME AS SUPPORT_ORG_NAME , DATE_FORMAT(E.REG_DATE, '%Y-%m-%d') AS REG_DT , U.USERNAME AS REG_USERNAME , F.FILE_SEQ , F.FILE_PATH , F.CRAWL_URL , F.THUMBNAIL_PATH , EB.EDU_MARK_SEQ , EW.EDU_WATCHING_SEQ FROM ST_EDUCATION E INNER JOIN ST_USER U ON E.REG_USER_SEQ = U.USER_SEQ inner join ST_CATEGORY1 as CATE1 ON CATE1.CATEGORY1_SEQ = E.CATEGORY_1 inner join ST_CATEGORY2 as CATE2 ON CATE2.CATEGORY2_SEQ = E.CATEGORY_2 inner join ST_CATEGORY3 as CATE3 ON CATE3.CATEGORY3_SEQ = E.CATEGORY_3 left join ST_SUPPORT as SUPPORT ON SUPPORT.SUPPORT_SEQ = E.SUPPORT_ORG LEFT JOIN ST_FILES F ON F.REF_TYPE = 'EDUCATION_IMAGE' AND F.REF_SEQ = E.EDU_SEQ AND F.DEL_FLAG = 0 left join ST_EDUCATION_BOOKMARK as EB ON EB.EDU_SEQ = E.EDU_SEQ and EB.USER_SEQ = #{login_user_seq} left join ST_EDUCATION_WATCHING as EW ON EW.EDU_SEQ = E.EDU_SEQ and EW.USER_SEQ = #{login_user_seq} WHERE E.DEL_FLAG = 0 AND E.EDU_URL != ''
AND E.SUBJECT LIKE CONCAT('%', #{keyword}, '%')
AND E.RECOMMEND_FLAG = 1
AND EW.EDU_WATCHING_SEQ is null
AND EW.EDU_WATCHING_SEQ is null
AND (E.CATEGORY_2 IN
#{item}
)
OR
(
E.CATEGORY_3 IN
#{item}
)
AND E.CATEGORY_1 = #{category1}
AND E.CATEGORY_2 = #{category2}
AND E.CATEGORY_3 = #{category2}
AND E.SUPPORT_ORG = #{support_org}
ORDER BY READ_CNT DESC, EDU_SEQ DESC /*||ORDER BY READ_CNT DESC, EDU_SEQ DESC||*/
ORDER BY RAND()
ORDER BY EDU_SEQ DESC /*||ORDER BY EDU_SEQ DESC||*/

/* file : EducationMapper.xml, id : getEducation */
  SELECT A.*
    FROM ST_EDUCATION A
   WHERE A.DEL_FLAG = 0
     AND A.EDU_SEQ  = #{eduSeq}

mapper.update:
/* file : EducationMapper.xml, id : updateEducationReadCnt */
  UPDATE ST_EDUCATION
     SET READ_CNT     = READ_CNT+1
   WHERE EDU_SEQ      = #{edu_seq}
