mapper.insert:
/* file : SurveyMapper.xml, id : insertUserQuestion */
INSERT
INTO ST_USER_QUESTION (
USER_SURVEY_SEQ
, QUESTION_SETTING_SEQ
, REG_USER_SEQ
, REG_DATE)
VALUES (
#{userSurveySeq}
, #{questionSettingSeq}
, #{loginUserSeq}
, CURRENT_TIMESTAMP())


    SELECT LAST_INSERT_ID()

